CREATE DATABASE IF NOT EXISTS yq DEFAULT CHARSET = utf8;

USE `yq`;

SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";


--
-- 表的结构 `donate`
--

CREATE TABLE `donate` (
  `id` int(11) NOT NULL,
  `goods` text NOT NULL,
  `wish` text,
  `donateDis` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

--
-- 表的结构 `hero`
--

CREATE TABLE `hero` (
  `id` int(11) NOT NULL,
  `heroname` varchar(20) NOT NULL COMMENT '抗疫英雄姓名',
  `birthday` varchar(40) DEFAULT NULL COMMENT '出生日期',
  `isshow` varchar(100) DEFAULT NULL COMMENT '是否展示',
  `photosrc` varchar(255) DEFAULT NULL COMMENT '头像连接',
  `h_like` int(11) DEFAULT NULL COMMENT '点赞数',
  `ptype` int(11) DEFAULT NULL COMMENT '属于类型',
  `h_describe` text COMMENT '英雄描述',
  `identify` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

